\* {
 box-sizing: border-box;
}
body {
 font-family: Phenomena, script;
 margin: 5vw;
 background: #111;
 color: #000;
 counter-reset: element;
}
.periodic-table {
 display: grid;
 grid-column-gap: .5vw;
 grid-row-gap: .5vw;
 padding-left: 0;
}
.periodic-table  li {
 display: flex;
 position: relative;
 justify-content: center;
 align-items: center;
 height: 4.5vw;
 border-radius: 3px;
 background: rgb(91, 194, 234);
 font-weight: bold;
}
.periodic-table  li:before {
 counter-increment: element; 
 content: counter(element);
 position: absolute;
 bottom: 5px;
 left: 6px;
 font-size: .9vw;
 font-weight: lighter;
}
.periodic-table  li abbr {
 font-size: 2.5vw;
}
#helium {
 grid-column-start: 18;
}
#boron, #aluminium {
 grid-column-start: 13;
}
#cerium, #thorium {
 grid-column-start: 4;
}
.periodic-table  li:nth-child(n+58):nth-last-child(n+48) {
 grid-row-start: 8;
}
.periodic-table  li:nth-child(n+90):nth-last-child(n+16) {
 grid-row-start: 9;
}
.periodic-table:before {
 display: block;
 content: '';
